import React from 'react';
import ArticleComment from './ArticleComment';

function CommentFeed() {
  return (
    <div>
    {[
        'User 1',
        'User 2',
        'User 3',
        'User 4',
        'User 5',
        'User 6',
        'User 7',
        'User 8',
      ].map((variant) => (
        <ArticleComment 
            bg={variant.toLowerCase()}
            key={variant}
            text={variant.toLowerCase() === 'light' ? 'dark' : 'white'}
            style={{ width: '18rem' }}
            className="mb-2"
        > {variant}
        </ArticleComment>
      ))}
    </div>
  );
}

export default CommentFeed;
