import React from 'react';
import ArticleComment from './ArticleComment';
import Col from 'react-bootstrap/Col';
import './style/CommentFeed.css';

function CommentFeed() {
  const posts = [
    {username: 'User 1', header: 'Header 1'},
    {username: 'User 2', header: 'Header 2'}
  ];
  return (
    <div class='horizontal-center'>
        <Col>
            {posts.map((post) => (
                <ArticleComment 
                  name = {post.username}
                  // header = {variant[1]} 
                  // bg={post.toLowerCase()}
                  key={post}
                  className="mb-2"
                > {post}
                </ArticleComment>
        ))  }
        </Col>
    </div>
  );
}

export default CommentFeed;
